/**
 * PMC Filters - js library to mimic simple wp add_filter & apply_filters for use on client side javascript
 *
 * To add a filter event:
 * pmc.hooks.add_filter( 'filter-name', function( data, p1, p2, ... ) {
 *   // some code....
 *   return data;
 * } );
 *
 * add_filter( 'filter-name', function( data, p1, p2, ... ) {
 *   // some code....
 *   return data;
 * } );
 *
 * To apply a data value throught a filter:
 * data = pmc.hooks.apply_filters( 'filter-name', data, p1, p2, ... );
 * data = apply_filters( 'filter-name', data, p1, p2, ... );
 *
 * @TODO: should integrate with core wp-hook.js when available
 * @ref: https://core.trac.wordpress.org/ticket/21170
 */
function add_filter(){pmc.hooks.add_filter.apply(pmc.hooks,arguments)}function apply_filters(){return pmc.hooks.apply_filters.apply(pmc.hooks,arguments)}var pmc=pmc||{}
pmc.hooks=pmc.hooks||{pmc_filters:[],apply_filters:function(){var t=[].shift.call(arguments)
if("undefined"!=!t&&"undefined"!=typeof arguments[0]){if(this.pmc_filters[t])for(filters=this.pmc_filters[t],i=0;i<filters.length;i++)if("function"==typeof filters[i].callback)try{arguments[0]=filters[i].callback.apply(null,arguments)}catch(t){}return arguments[0]}},add_filter:function(t,i){if("function"==typeof i&&""!=typeof t)try{"object"!=typeof this.pmc_filters[t]&&(this.pmc_filters[t]=[]),this.pmc_filters[t].push({filter:t,callback:i})}catch(t){}}}
